dict.string:
function run(argv) {
	const [source, toggle_to] = argv[0].split('|')

	const system_preferences = Application('System Preferences')
	const system_events = Application('System Events')
	const prefs_running = system_preferences.running()

	system_preferences.panes.byId('com.apple.preference.battery').anchors.byName(source).reveal()

	delay(2) // Give the pane time to load

	const toggle_checkbox = system_events.processes.byName('System Preferences').windows.byName('Battery').groups[0].checkboxes.byName('Low power mode')

	if (toggle_checkbox.value() === 0 && toggle_to === 'on' || toggle_checkbox.value() === 1 && toggle_to === 'off') toggle_checkbox.click()

	if (!prefs_running) system_preferences.quit()
}
